  --shadow-2xs: 0px 1px 2px 0px hsl(220 13% 4% / 0.3);
  --shadow-xs: 0px 1px 3px 0px hsl(220 13% 4% / 0.4), 0px 1px 2px 0px hsl(220 13% 4% / 0.3);
  --shadow-sm: 0px 1px 3px 0px hsl(220 13% 4% / 0.4), 0px 1px 2px 0px hsl(220 13% 4% / 0.3);
  --shadow: 0px 1px 3px 0px hsl(220 13% 4% / 0.4), 0px 1px 2px 0px hsl(220 13% 4% / 0.3);
  --shadow-md: 0px 4px 6px -1px hsl(220 13% 4% / 0.4), 0px 2px 4px -1px hsl(220 13% 4% / 0.3);
